document.addEventListener("DOMContentLoaded",()=>{let t=document.getElementById("popup");var e=document.getElementById("show-popup"),d=document.querySelector(".close-button");e.addEventListener("click",()=>{t.classList.remove("hidden")}),d.addEventListener("click",()=>{t.classList.add("hidden")}),t.addEventListener("click",e=>{e.target===t&&t.classList.add("hidden")})});